$(document).ready(function(){var c=$(".product-slider ul");var b=$(".product-slider li");var a=function(){var d=$(".product-slider").width();var e=$(window).width();var g=$(".product-slider ul");var f=$(".product-slider li");g.stop().animate({marginLeft:"0"},0);scrolltimes=f.not(".clone").size();scrollcount=0;if(e>999){f.css("width",d/5-12)}else{if(e>700&&e<999){f.css("width",d/4-20)}else{if(e>550&&e<699){f.css("width",d/3-23)}else{f.css("width",d/2-27)}}}g.css("width",(scrolltimes*2+1)*f.width())};$(window).resize(a);c.append(c.html().replace(/<li>/g,'<li class="clone">'));a();$(".product-slider .arrow-left").on("click",function(){scrollwidth=b.width()+18;scrolltimes=b.not(".clone").size();if(scrollcount==0){c.animate({marginLeft:scrollwidth*scrolltimes*-1+"px"},0);scrollcount=scrolltimes}c.animate({marginLeft:"+="+scrollwidth+"px"},200);scrollcount--});$(".product-slider .arrow-right").on("click",function(){scrollwidth=b.width()+18;scrolltimes=b.not(".clone").size();if(scrollcount==scrolltimes){c.animate({marginLeft:"0px"},0);scrollcount=0}c.animate({marginLeft:"-="+scrollwidth+"px"},200);scrollcount++})});